import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskList from './Components/TaskList';
import TaskForm from './Components/TaskForm';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [refreshFlag, setRefreshFlag] = useState(false);
  const [editingTask, setEditingTask] = useState(null); // New state for editing

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [refreshFlag]);

  const handleTaskAdded = (newTask) => {
    setTasks(currentTasks => [...currentTasks, newTask]);
    setRefreshFlag(prev => !prev);
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      setTasks(currentTasks => currentTasks.filter(task => task._id !== id));
      setRefreshFlag(prev => !prev);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleToggleComplete = async (id, completed) => {
    try {
      await axios.patch(`http://localhost:5000/tasks/${id}/status`, { completed });
      setTasks(currentTasks =>
        currentTasks.map(task =>
          task._id === id ? { ...task, completed } : task
        )
      );
      setRefreshFlag(prev => !prev);
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task); // Set the task to be edited
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1 style={{ fontSize: '2em', fontWeight: 'bold', marginBottom: '20px' }}>Task Mate</h1>
      <TaskForm onTaskAdded={handleTaskAdded} />
      <TaskList
        tasks={tasks}
        onDelete={handleDeleteTask}
        onToggleComplete={handleToggleComplete}
        onEdit={handleEdit} // Pass handleEdit to TaskList
        key={refreshFlag}
      />
      {editingTask && (
        <div>
          <h2>Edit Task</h2>
          {/* We'll add the edit form here in the next step */}
        </div>
      )}
    </div>
  );
}

export default App;
