import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [refreshFlag, setRefreshFlag] = useState(false); // New state

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [refreshFlag]); // Re-run effect when refreshFlag changes

  const handleTaskAdded = (newTask) => {
    setTasks(currentTasks => [...currentTasks, newTask]);
    setRefreshFlag(prev => !prev); // Toggle the refresh flag
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-bold mb-8">Task Mate</h1>
      <TaskForm onTaskAdded={handleTaskAdded} />
      <TaskList tasks={tasks} key={refreshFlag} /> {/* Add key prop */}
    </div>
  );
}

export default App;
