import React, { useState, useEffect } from 'react';

const EditTaskForm = ({ task, onSave, onCancel }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [category, setCategory] = useState('');

  useEffect(() => {
    if (task) {
      setTitle(task.title);
      setDescription(task.description || '');
      setDueDate(task.dueDate ? task.dueDate.slice(0, 10) : '');
      setCategory(task.category || '');
    }
  }, [task]);

  const handleSubmit = (event) => {
    event.preventDefault();
    onSave({ _id: task._id, title, description, dueDate, category });
  };

  return (
    <div style={{ backgroundColor: '#f9f9f9', padding: '20px', marginBottom: '20px', borderRadius: '5px', border: '1px solid #eee' }}>
      <h2 style={{ fontSize: '1.3em', fontWeight: 'bold', marginBottom: '15px' }}>Edit Task</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div>
          <label htmlFor="title" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Title:</label>
          <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required style={{ padding: '8px', borderRadius: '3px', border: '1px solid #ccc', width: '100%', boxSizing: 'border-box' }} />
        </div>
        <div>
          <label htmlFor="description" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Description:</label>
          <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)} style={{ padding: '8px', borderRadius: '3px', border: '1px solid #ccc', width: '100%', boxSizing: 'border-box' }} />
        </div>
        <div>
          <label htmlFor="dueDate" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Due Date:</label>
          <input type="date" id="dueDate" value={dueDate} onChange={(e) => setDueDate(e.target.value)} style={{ padding: '8px', borderRadius: '3px', border: '1px solid #ccc', width: '100%', boxSizing: 'border-box' }} />
        </div>
        <div>
          <label htmlFor="category" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Category:</label>
          <input type="text" id="category" value={category} onChange={(e) => setCategory(e.target.value)} style={{ padding: '8px', borderRadius: '3px', border: '1px solid #ccc', width: '100%', boxSizing: 'border-box' }} />
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button type="submit" style={{ backgroundColor: 'green', color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>Save</button>
          <button type="button" onClick={onCancel} style={{ backgroundColor: 'gray', color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default EditTaskForm;
