import React from 'react';
import './TaskItem.css'

const TaskItem = ({ task, onDelete, onToggleComplete, onEdit }) => {
  return (
    <div style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
      <div>
        <input
          type="checkbox"
          checked={task.completed}
          onChange={() => onToggleComplete(task._id, !task.completed)}
          style={{ marginRight: '10px' }}
        />
        <h3 style={{ fontSize: '1.2em', fontWeight: 'bold', textDecoration: task.completed ? 'line-through' : '', color: task.completed ? 'gray' : 'black' }}>
          {task.title}
        </h3>
        <p style={{ color: '#666' }}>{task.description}</p>
        <p style={{ fontSize: '0.8em', color: '#888' }}>Due Date: {task.dueDate}</p>
        <p style={{ fontSize: '0.8em', color: '#888' }}>Category: {task.category}</p>
      </div>
      <div>
        <button
          onClick={() => onEdit(task)}
          style={{ backgroundColor: 'blue', color: 'white', border: 'none', padding: '8px 12px', borderRadius: '5px', cursor: 'pointer', marginRight: '5px' }}
        >
          Edit
        </button>
        <button
          onClick={() => onDelete(task._id)}
          style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '8px 12px', borderRadius: '5px', cursor: 'pointer' }}
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default TaskItem;
