import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import TaskList from './Components/TaskList';
import TaskForm from './Components/TaskForm';
import EditTaskForm from './Components/EditTaskForm';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [refreshFlag, setRefreshFlag] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [availableCategories, setAvailableCategories] = useState([]);
  const editFormRef = useRef(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
        const categories = [...new Set(response.data.map(task => task.category).filter(Boolean))];
        setAvailableCategories(['', ...categories]);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [refreshFlag]);

  const handleTaskAdded = (newTask) => {
    setTasks(currentTasks => [...currentTasks, newTask]);
    setRefreshFlag(prev => !prev);
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      setTasks(currentTasks => currentTasks.filter(task => task._id !== id));
      setRefreshFlag(prev => !prev);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleToggleComplete = async (id, completed) => {
    try {
      await axios.patch(`http://localhost:5000/tasks/${id}/status`, { completed });
      setTasks(currentTasks =>
        currentTasks.map(task =>
          task._id === id ? { ...task, completed } : task
        )
      );
      // Removed: setRefreshFlag(prev => !prev);
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setTimeout(() => {
      if (editFormRef.current) {
        editFormRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
      } else {
        console.log("editFormRef.current is still null after delay.");
      }
    }, 100);
  };

  const handleSaveEdit = async (updatedTask) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${updatedTask._id}`, updatedTask);
      setTasks(currentTasks =>
        currentTasks.map(task =>
          task._id === updatedTask._id ? updatedTask : task
        )
      );
      setEditingTask(null);
      setRefreshFlag(prev => !prev);
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setFilterCategory(event.target.value);
  };

  useEffect(() => {
    const fetchFilteredTasks = async () => {
      try {
        let url = 'http://localhost:5000/tasks';
        if (searchQuery) {
          url = `http://localhost:5000/tasks/search?query=${searchQuery}`;
        } else if (filterCategory) {
          url = `http://localhost:5000/tasks/category/${filterCategory}`;
        }
        const response = await axios.get(url);
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching filtered tasks:', error);
      }
    };

    fetchFilteredTasks();
  }, [searchQuery, filterCategory]);

  // Sort tasks by due date (closest first)
  const sortedTasks = [...tasks].sort((a, b) => {
    if (!a.dueDate) return 1; // Tasks without due date go to the end
    if (!b.dueDate) return -1;
    return new Date(a.dueDate) - new Date(b.dueDate);
  });

  return (
    <div className="app-container">
      <nav className="navbar">
        <select
          value={filterCategory}
          onChange={handleCategoryChange}
          className="category-dropdown"
        >
          <option value="">All Categories</option>
          {availableCategories.map(category => (
            <option key={category} value={category}>{category || 'No Category'}</option>
          ))}
        </select>
        <h1 className="app-title">Task Mate</h1>
        <input
          type="text"
          placeholder="Search tasks..."
          value={searchQuery}
          onChange={handleSearchChange}
          className="search-input"
        />
      </nav>
      <div className="add-form-container">
        <TaskForm onTaskAdded={handleTaskAdded} />
      </div>
      {editingTask && (
        <div className="edit-task-form-container" ref={editFormRef}>
          <EditTaskForm
            task={editingTask}
            onSave={handleSaveEdit}
            onCancel={handleCancelEdit}
          />
        </div>
      )}
      <div className="task-list-container">
        <h2>Task List</h2>
        <div className="task-grid">
          <TaskList
            tasks={sortedTasks} // Use the sortedTasks array
            onDelete={handleDeleteTask}
            onToggleComplete={handleToggleComplete}
            onEdit={handleEdit}
            key={refreshFlag} // Keep the key for forced refresh on add/delete/edit
          />
        </div>
      </div>
    </div>
  );
}

export default App;
