import React from 'react';

const TaskItem = ({ task, onDelete, onToggleComplete }) => {
  return (
    <div className="bg-white shadow-md rounded-md p-4 mb-4 flex items-center justify-between">
      <div className="flex items-center">
        <input
          type="checkbox"
          className="mr-2 form-checkbox h-5 w-5 text-green-500 focus:ring-green-500 border-gray-300 rounded"
          checked={task.completed}
          onChange={() => onToggleComplete(task._id, !task.completed)}
        />
        <div>
          <h3 style={{ textDecoration: task.completed ? 'line-through' : '', color: task.completed ? 'gray' : '' }} className="text-lg font-semibold">
            {task.title}
          </h3>
          <p className="text-gray-600">{task.description}</p>
          <p className="text-sm text-gray-500">Due Date: {task.dueDate}</p>
          <p className="text-sm text-gray-500">Category: {task.category}</p>
        </div>
      </div>
      <button
        onClick={() => onDelete(task._id)}
        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
      >
        Delete
      </button>
    </div>
  );
};

export default TaskItem;
